// Get emails from the trigger
const emails = $("Watch for HARO Emails").all();

// Get processed email IDs from database
const processedEmailIds = $("Get Processed HARO Email IDs").all().map(item => item.json.email_id);

// Filter out emails whose id exists in the processed list
const unprocessedEmails = emails.filter(email => {
  return !processedEmailIds.includes(email.json.id);
});

return unprocessedEmails;

const twentyFourHoursFromNow = new Date(Date.now() + 24 * 60 * 60 * 1000);

return $input.all().flatMap(item =>
  (item.json.output.opportunities || [])
    .filter(opp => {
      if (!opp.deadline) return true;
      const dl = new Date(opp.deadline);
      // Keep only if deadline is invalid (unparseable) or more than 24h away
      return isNaN(dl.getTime()) || dl >= twentyFourHoursFromNow;
    })
    .map(opp => ({ json: opp }))
);

const crypto = require('crypto');
const item = $json;
const emailId = $('Get a message').first().json.id;
const userEmail = $('Get a message').first().json.to || 'default';
const oppId = crypto.randomUUID();

return [{
  json: {
    pageContent: item.query,
    metadata: {
      opportunity_id: oppId,
      user_email: userEmail,
      match_reason: item.matchReason,
      deadline: item.deadline,
      contact_info: item.contactInfo,
      email_id: emailId,
      generated_subject: item.output ? item.output.subject : '',
      generated_message: item.output ? item.output.message : '',
      feedback_rating: null
    }
  }
}];

