const checks = $input.all();
const ips = $('Generate IP Candidates').all();

for (let i = 0; i < checks.length; i++) {
  if (checks[i].json.items !== undefined) {
    return [{ json: { beeperHost: 'http://' + ips[i].json.ip + ':23373' } }];
  }
}

throw new Error('Beeper Desktop not reachable on any configured IP. Ensure it is running with Remote Access enabled.');

const timeZone = 'America/New_York'; // Change to your timezone
const today = new Date().toLocaleDateString('en-US', { timeZone });
const [todayMonth, todayDay] = today.split('/').map(Number);

return $input.all()
  .filter(item => {
    const phone = String(item.json.Phone || '').trim();
    const bday = String(item.json.Birthday || '').trim();
    if (!phone || !bday) return false;

    // Strip invisible characters then split on / or -
    const clean = bday.replace(/[^\d\/-]/g, '');
    const parts = clean.split(/[\/\-]/);

    let month, day;
    if (parts.length === 3) {
      if (parts[0].length === 4) {
        // YYYY-MM-DD
        month = parseInt(parts[1], 10);
        day = parseInt(parts[2], 10);
      } else {
        // MM/DD/YYYY
        month = parseInt(parts[0], 10);
        day = parseInt(parts[1], 10);
      }
    } else if (parts.length === 2) {
      // MM-DD or MM/DD
      month = parseInt(parts[0], 10);
      day = parseInt(parts[1], 10);
    } else {
      return false;
    }

    return month === todayMonth && day === todayDay;
  })
  .map(item => {
    let phone = String(item.json.Phone).trim();
    if (!phone.startsWith('+')) phone = '+' + phone;
    return { json: { ...item.json, Phone: phone } };
  });

const searches = $input.all();
const people = $('Filter Todays Birthdays').all();
const host = $('Select Live Host').first().json.beeperHost;

const directTemplates = [
  (name) => `Happy Birthday, ${name}! \ud83c\udf82 Hope your day is as sweet as cake!`,
  (name) => `Happy Birthday ${name}!!! \ud83c\udf89\ud83c\udf89\ud83c\udf89`,
  (name) => `It's ${name}'s birthday! \ud83c\udf88\ud83c\udf88 Sending you all the balloons!`,
  (name) => `HBD ${name}! \ud83c\udf70 Wishing you the best year yet!`,
  (name) => `Happy Birthday, ${name}! \ud83c\udf1f Hope it's a great one!`,
  (name) => `Wishing you a wonderful birthday, ${name}! \ud83e\udd73\ud83c\udf8a`,
  (name) => `Happy Birthday ${name}! \ud83c\udf82\ud83c\udf88\ud83c\udf81 Party time!`,
  (name) => `Hey ${name}, happy birthday!! \ud83e\udd42 Cheers to another year!`
];

return searches.map((search, i) => {
  const person = people[i].json;
  const items = search.json.items || [];
  const chatID = items.length > 0 ? items[0].id : null;
  const relay = String(person.Relay || '').toLowerCase() === 'true' || person.Relay === true;
  let message;
  if (relay) {
    message = `Tell ${person.Name.split(" ")[0]} I said happy birthday!`;
  } else {
    const template = directTemplates[Math.floor(Math.random() * directTemplates.length)];
    message = template(person.Name);
  }

  const dinner = String(person.Dinner || '').toLowerCase() === 'true' || person.Dinner === true;

  const dinnerTemplates = [
    'Do you have any dinner plans tonight?',
    'Want to grab dinner tonight to celebrate?',
    'Any birthday dinner plans? I\'m down to celebrate!',
    'Doing anything special for dinner tonight?',
    'Got any dinner plans for your big day?',
    'Birthday dinner tonight? I\'m in!'
  ];
  const dinnerMessage = dinnerTemplates[Math.floor(Math.random() * dinnerTemplates.length)];

  return {
    json: {
      chatID: chatID ? encodeURIComponent(chatID) : null,
      chatFound: chatID !== null,
      name: person.Name,
      phone: person.Phone,
      beeperHost: host,
      message: message,
      dinner: dinner,
      dinnerMessage: dinnerMessage
    }
  };
});

// Change this to your preferred network (e.g. 'iMessage', 'WhatsApp', 'Google Messages')
const preferredNetwork = 'iMessage';

const accounts = $('Get Accounts').all().map(a => a.json);
let account = accounts.find(a => a.network === preferredNetwork);
if (!account) account = accounts[0];
if (!account) throw new Error('No Beeper accounts found. Ensure at least one account is connected.');

const data = $('Extract Chat ID').item.json;

return [{
  json: {
    accountID: account.accountID,
    phone: data.phone,
    beeperHost: data.beeperHost,
    message: data.message,
    name: data.name,
    dinner: data.dinner,
    dinnerMessage: data.dinnerMessage
  }
}];

const timeZone = 'America/New_York';
const today = new Date().toLocaleDateString('en-US', { timeZone });
const [todayMonth, todayDay] = today.split('/').map(Number);

const missedDates = [];
for (let i = 1; i <= 7; i++) {
  const d = new Date();
  d.setDate(d.getDate() - i);
  const ds = d.toLocaleDateString('en-US', { timeZone });
  const [m, day] = ds.split('/').map(Number);
  missedDates.push({ month: m, day: day, daysLate: i });
}

return $input.all()
  .map(item => {
    const phone = String(item.json.Phone || '').trim();
    const bday = String(item.json.Birthday || '').trim();
    if (!phone || !bday) return null;

    const clean = bday.replace(/[^\d\/-]/g, '');
    const parts = clean.split(/[\/\-]/);

    let month, day;
    if (parts.length === 3) {
      if (parts[0].length === 4) {
        month = parseInt(parts[1], 10);
        day = parseInt(parts[2], 10);
      } else {
        month = parseInt(parts[0], 10);
        day = parseInt(parts[1], 10);
      }
    } else if (parts.length === 2) {
      month = parseInt(parts[0], 10);
      day = parseInt(parts[1], 10);
    } else {
      return null;
    }

    const match = missedDates.find(d => d.month === month && d.day === day);
    if (!match) return null;

    let phoneClean = phone;
    if (!phoneClean.startsWith('+')) phoneClean = '+' + phoneClean;

    return { json: { ...item.json, Phone: phoneClean, daysLate: match.daysLate } };
  })
  .filter(item => item !== null);

const missedBirthdays = $('Filter Missed Birthdays').all();
const logEntries = $input.all();
const eightDaysAgo = new Date(Date.now() - 8 * 24 * 60 * 60 * 1000);

const unsent = missedBirthdays.filter(mb => {
  const phone = String(mb.json.Phone || '').trim();
  return !logEntries.some(log => {
    const logPhone = String(log.json.Phone || '').trim();
    const logType = String(log.json.Type || '').toLowerCase();
    const logDate = new Date(log.json.Date);
    return logPhone === phone
      && logType.includes('birthday')
      && logDate >= eightDaysAgo;
  });
});

return unsent.map(mb => ({ json: { ...mb.json } }));

const searches = $input.all();
const people = $('Find Unsent Birthdays').all();
const host = $('Select Live Host').first().json.beeperHost;

const belatedTemplates = [
  (name) => `Happy belated birthday, ${name}! Hope you had an amazing day!`,
  (name) => `Hey ${name}, happy late birthday! Sorry I missed it!`,
  (name) => `Better late than never \u2014 happy birthday, ${name}!`,
  (name) => `Happy belated, ${name}! Hope your birthday was great!`,
  (name) => `Late happy birthday, ${name}! Wishing you the best!`,
  (name) => `Happy belated birthday ${name}!! Hope it was a good one!`
];

return searches.map((search, i) => {
  const person = people[i].json;
  const items = search.json.items || [];
  const chatID = items.length > 0 ? items[0].id : null;
  const relay = String(person.Relay || '').toLowerCase() === 'true' || person.Relay === true;
  let message;
  if (relay) {
    message = `Tell ${person.Name.split(' ')[0]} I said happy belated birthday!`;
  } else {
    const template = belatedTemplates[Math.floor(Math.random() * belatedTemplates.length)];
    message = template(person.Name);
  }

  return {
    json: {
      chatID: chatID ? encodeURIComponent(chatID) : null,
      chatFound: chatID !== null,
      name: person.Name,
      phone: person.Phone,
      beeperHost: host,
      message: message
    }
  };
});

