// N8N Code Node - Create Nested Structure for Related Queries
// This code creates a nested structure: Topic -> related queries
// Get the input data (assuming it's the first item)
const inputData = $input.all()[0].json;
// Initialize arrays to store extracted data
let relatedQueries = [];
let risingQueries = [];
try {
  // Check if the response contains related_queries data
  if (inputData.related_queries) {

    // Extract "top" related queries if they exist
    if (inputData.related_queries.top) {
      relatedQueries = 
        inputData.related_queries.top.map((query, index) => ({
          query: query.query,
          value: query.value,
          extracted_value: query.extracted_value,
          link: query.link,
          serpapi_link: query.serpapi_link,
          type: 'top',
          rank: index + 1
        }));
    }

    // Extract "rising" related queries if they exist
    if (inputData.related_queries.rising) {
      risingQueries = 
        inputData.related_queries.rising.map((query, index) => ({
          query: query.query,
          value: query.value,
          extracted_value: query.extracted_value,
          link: query.link,
          serpapi_link: query.serpapi_link,
          type: 'rising',
          rank: index + 1
        }));
    }
  }

  // Combine all queries with their types
  const allQueries = [...relatedQueries, ...risingQueries];

  // Sort by extracted_value (descending) to get top performers
  const sortedQueries = allQueries.sort((a, b) => {
    const aVal = typeof a.extracted_value === 'number' ? a.extracted_value : 0;
    const bVal = typeof b.extracted_value === 'number' ? b.extracted_value : 0;
    return bVal - aVal;
  });

  // Get top 10 queries
  const top10Queries = sortedQueries.slice(0, 10);

  // Return only top 10 queries
  return [
    {
      json: {
        topic: inputData.search_parameters?.q || 'Unknown',
        top_10_queries: top10Queries
      }
    }
  ];

} catch (error) {
  // Handle errors gracefully
  return [
    {
      json: {
        error: 'Failed to extract and structure related queries data',
        error_message: error.message,
        topic: inputData.search_parameters?.q || 'Unknown'
      }
    }
  ];
}

