// Initialiser le tableau de sortie
let restructuredData = [];

// Parcourir chaque query dans l'objet
Object.keys(inputData).forEach(key => {
  const queryData = inputData[key];

  // Vérifier que queryData existe et contient au moins 2 éléments non vides
  if (queryData && 
      Array.isArray(queryData) && 
      queryData.length >= 2 && 
      queryData[0] && 
      queryData[0].trim() !== "" && 
      queryData[1] && 
      queryData[1].trim() !== "") {

    restructuredData.push({
      Query: queryData[0],           // Le nom de la requête
      Évolution: queryData[1],       // Le pourcentage d'évolution
      News: "",                       // Colonne vide pour l'instant
      Idée: ""                        // Colonne vide pour l'instant
    }); 
  }
});

// Retourner le tableau restructuré
return restructuredData.map(item => ({ json: item }));

