  if (item.json.Query) {
    // Attribution des queries selon leur position (index)
    // On ne garde que la valeur de la colonne Query
    if (index === 0) {
      query1.push(item.json.Query);
    } else if (index === 1) {
      query2.push(item.json.Query);
    } else if (index === 2) {
      query3.push(item.json.Query);
    }
  }
});

