// Booming Bookkeeping Pricing Calculator
const input = $input.first().json;

const businessName = input['Business Name'];
const prospectEmail = input['Prospect Email'];
const numTransactions = parseInt(input['Number of Transactions (Monthly)']) || 0;
const numAccounts = parseInt(input['Number of Bank/Credit Card Accounts']) || 0;
const needsArAp = input['Needs AR or AP Services'];
const serviceType = input['Service Type'];
const monthsCleanup = parseInt(input['Months of Cleanup Needed']) || 0;
const num1099s = parseInt(input['Number of 1099s to File']) || 0;
const testMode = input['Test Mode'];

// Pricing constants based on Booming Bookkeeping rates
const PRICING = {
  DIY_MEMBERSHIP: 49,
  QUICKBOOKS_SETUP: 199.99,
  QUARTERLY_REVIEW: 447,
  COMPREHENSIVE: 499.99,
  ADVANCED: 999.99,
  CLEANUP_MINIMUM: 500,
  PER_1099: 20
};

const lineItems = [];
let totalAmount = 0;

// Determine if needs Advanced tier (A/R or A/P services)
const needsAdvanced = needsArAp && !needsArAp.includes('No');

// Calculate based on service type
if (serviceType.includes('Monthly Bookkeeping')) {
  const monthlyRate = needsAdvanced ? PRICING.ADVANCED : PRICING.COMPREHENSIVE;
  const tierName = needsAdvanced ? 'Advanced Bookkeeping Services' : 'Comprehensive Bookkeeping Services';
  const description = needsAdvanced 
    ? 'Monthly bookkeeping with A/R, A/P management, payroll integration, sales tax support, and custom reporting. Deliverables guaranteed by the 15th.'
    : 'Monthly bank/credit card reconciliation, transaction categorization, financial reports (P&L, Balance Sheet), and monthly check-ins. Deliverables guaranteed by the 15th.';

  lineItems.push({
    LineNum: 1,
    Description: tierName + ' - ' + description,
    Amount: monthlyRate,
    DetailType: 'SalesItemLineDetail'
  });
  totalAmount += monthlyRate;
}

if (serviceType.includes('QuickBooks Setup')) {
  lineItems.push({
    LineNum: lineItems.length + 1,
    Description: 'QuickBooks Setup Package - QBO setup, Chart of Accounts customization, bank integration, customer/vendor import, initial training session. Includes $15/month discount on DIY Membership.',
    Amount: PRICING.QUICKBOOKS_SETUP,
    DetailType: 'SalesItemLineDetail'
  });
  totalAmount += PRICING.QUICKBOOKS_SETUP;
}

if (serviceType.includes('Quarterly Review')) {
  lineItems.push({
    LineNum: lineItems.length + 1,
    Description: 'Quarterly Review & Insights Package - Comprehensive quarterly review, reconciliation audit, financial statement analysis, 60-90 min strategy session, summary report, and email support.',
    Amount: PRICING.QUARTERLY_REVIEW,
    DetailType: 'SalesItemLineDetail'
  });
  totalAmount += PRICING.QUARTERLY_REVIEW;
}

if (serviceType.includes('DIY Bookkeeping')) {
  lineItems.push({
    LineNum: lineItems.length + 1,
    Description: 'DIY Bookkeeping Membership - Access to educational resources, templates, community forum, monthly Q&A sessions, and discounted consulting rates.',
    Amount: PRICING.DIY_MEMBERSHIP,
    DetailType: 'SalesItemLineDetail'
  });
  totalAmount += PRICING.DIY_MEMBERSHIP;
}

if (serviceType.includes('Cleanup Services') && monthsCleanup > 0) {
  const monthlyRate = needsAdvanced ? PRICING.ADVANCED : PRICING.COMPREHENSIVE;
  const cleanupCost = Math.max(PRICING.CLEANUP_MINIMUM, monthlyRate * monthsCleanup);

  lineItems.push({
    LineNum: lineItems.length + 1,
    Description: `Cleanup Services (${monthsCleanup} months) - Historical transaction catch-up, Chart of Accounts optimization, error correction, and retrospective financial statements.`,
    Amount: cleanupCost,
    DetailType: 'SalesItemLineDetail'
  });
  totalAmount += cleanupCost;
}

// Add 1099 filing if applicable
if (num1099s > 0) {
  const filing1099Cost = num1099s * PRICING.PER_1099;
  lineItems.push({
    LineNum: lineItems.length + 1,
    Description: `1099 Filing Service (${num1099s} forms via QuickBooks)`,
    Amount: filing1099Cost,
    DetailType: 'SalesItemLineDetail'
  });
  totalAmount += filing1099Cost;
}

return {
  json: {
    businessName,
    prospectEmail,
    numTransactions,
    numAccounts,
    needsAdvanced,
    serviceType,
    monthsCleanup,
    num1099s,
    testMode,
    isTestMode: testMode && testMode.includes('Yes'),
    lineItems,
    totalAmount,
    estimateSummary: `Estimate for ${businessName}: $${totalAmount.toFixed(2)}`
  }
};

