// Extract data from LinkedIn API response
const inputData = $input.all();
// Process each item in the input data
const results = [];
for (const item of inputData) {
    // Handle the actual structure - data is directly in item.json.data
    const posts = item.json?.data || [];

    for (const post of posts) {
        // Extract LinkedIn profile URL and URN
        const profileUrl = post.author?.url || '';
        const profileUrn = post.author?.urn || '';

        // Extract person's name information
        const firstName = post.author?.firstName || '';
        const lastName = post.author?.lastName || '';
        const fullName = firstName && lastName ? `${firstName} ${lastName}` : '';

        // Extract post URL and URN
        const postUrl = post.postUrl || '';
        const postUrn = post.urn || '';

        // Extract timestamp and published date
        const timestamp = post.postedDateTimestamp || 0;
        const publishedDate = post.postedDate || '';

        // Extract reactions and comments
        const reactions = post.totalReactionCount || 0;
        const comments = post.commentsCount || 0;

        // Extract post content/text
        const postContent = post.text || '';

        // Calculate 14 days from publish date
        let twoWeeksFromPublish = '';
        let dateCreated = '';

        if (timestamp) {
            const publishDate = new Date(timestamp);
            const twoWeeksLater = new Date(publishDate.getTime() + (14 * 24 * 60 * 60 * 1000));
            twoWeeksFromPublish = twoWeeksLater.toISOString().split('T')[0] + 'T' + twoWeeksLater.toISOString().split('T')[1];

            // Create clean date format MM/DD/YYYY
            const month = String(publishDate.getMonth() + 1).padStart(2, '0');
            const day = String(publishDate.getDate()).padStart(2, '0');
            const year = publishDate.getFullYear();
            dateCreated = `${month}/${day}/${year}`;
        }

        // Create result object
        results.push({
            linkedinProfileUrl: profileUrl,
            linkedinProfileUrn: profileUrn,
            firstName: firstName,
            lastName: lastName,
            fullName: fullName,
            postUrl: postUrl,
            postUrn: postUrn,
            timestamp: timestamp,
            publishedDate: publishedDate,
            dateCreated: dateCreated, // New clean date field in MM/DD/YYYY format
            postContent: postContent, // LinkedIn post text content
            reactions: reactions,
            comments: comments,
            twoWeeksFromPublishDate: twoWeeksFromPublish
        });
    }
}
return results;

// Random Time Generator - Returns single random time object

// Function to generate random seconds with decimal places
function generateRandomSeconds(min = 0.1, max = 60, decimalPlaces = 1) {
  const randomValue = Math.random() * (max - min) + min;
  return Number(randomValue.toFixed(decimalPlaces));
}

// Generate single random time
const seconds = generateRandomSeconds(0.1, 60, 1);

// Return array with single object (n8n requirement)
return [{
  seconds: seconds,
  formatted: `${seconds} seconds`,
  milliseconds: seconds * 1000
}];
